public class Casteo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1;
		int b = 2;
		/*a esto se le llama cast, es una transformaci√≥n de un tipo de datos a otro
		tipo.*/
		System.out.println("Tu resultado en casteo(float) es: "+(float)a/b);
	
		System.out.println(("Tu resultado en casteo(int) es: "+(int)4.5)); // 4
		System.out.println(("Tu resultado en casteo(float) es: "+(float) 4)); // 4.0

	}

}
